import React , {Component} from 'react'
import {BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom'
import withNavigation from './WithNavigation.jsx' 
import withParams from './WithParams.jsx'

class TodoApp extends Component{
    render(){
        const LoginComponentWithNavigation= withNavigation(LoginComponent);
        const WelcomeComponentWithParams= withParams(WelcomeComponent);
        return (
            <div className='TodoApp'>
                <Router>
                    <HeaderComponent/>
                    <Routes>
                        <Route path="/" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/login" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/welcome/:name" element={<WelcomeComponentWithParams/>}/>
                        <Route path="/todos" element={<ListTodosComponent/>}/>
                        <Route path="/t" element={<ListTodosComponent/>}/>
                        <Route path="*" element ={<ErrorComponent/>}/>
                    </Routes>
                    <FooterComponent/>
                </Router>
            </div>
        )
    }    
}

class HeaderComponent extends Component{
    render(){
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a HREF="http://www.in28minutes.com" className='navbar-brand'>in28minutes</a></div>
                    <ul className="navbar-nav">
                        <li><Link className="nav-link" to="/welcome/in28minutes">Home</Link></li>
                        <li><Link className="nav-link" to="/todos">Todos</Link></li>
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li><Link className="nav-link" to="/login">Login</Link></li>
                        <li><Link className="nav-link" to="/logout">Login</Link></li>
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component{
    render(){
        return (
            <div>
                 <hr/> Footer
            </div>
        )
    }
}

class LogoutComponent extends Component{
    render(){
        return (
            <div>
                 <h1> You are logged out</h1>
                 <div className="container">
                     Thank you for using our Application.
                 </div>
            </div>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props){
        super(props)    
        this.state = {
            todos: 
            [{id: 1, description: 'Learn Raect', done: false , targetDate: new Date()},
             {id: 2, description: 'Learn Dance', done: false , targetDate: new Date()}
            ]
        }
    }
    render(){
        return( <div>
                    <h1> List Todos</h1>
                    <table>
                        <thead>
                            <tr>
                                <th>id</th>
                                <th>description</th>
                                <th>TargetDate</th>
                                <th>Is completed?</th>
                            </tr>
                        </thead>
                        <tbody>
                         {
                            this.state.todos.map (
                                todo=> 
                            <tr>
                                <td>{todo.id}</td>
                                <td>{todo.description}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.targetDate.toString()}</td>
                            </tr>
                            )
                         }
                        </tbody>
                    </table>
                 </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return <div> welcome {this.props.params.name}. You can manage your todos <Link to="/todos">here</Link> </div>
    }
}

class LoginComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            username: 'in28minutes',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }
    }


    handleChange= (event) => {
        console.log(event.target.value);
        this.setState(
            {
                [event.target.name]:event.target.value
            }
            )
    }

    loginClicked = (event) =>{
        if(this.state.username ==='in28minutes' && this.state.password==='dummy'){
            this.props.navigate(`/welcome/${this.state.username}`)
            //this.setState({showSuccessMessage: true});
            //this.setState({hasLoginFailed: false});
        }
        else{
            this.setState({showSuccessMessage: false});
            this.setState({hasLoginFailed: true});
        }
    }

    // handlePasswordChange= (event) => {
    //     console.log(event.target.value);
    //     this.setState({password:event.target.value})
    // }

    render(){
        return(
        <div>
            {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>*/}
            {this.state.hasLoginFailed && <div> Invalid Credentials</div>}
            {/*<ShowLoginSuccessful showSuccessMessage={this.state.showSuccessMessage}/>*/}
            {this.state.showSuccessMessage && <div> Login Successful</div>}
            Username: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
            Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
            <button onClick={this.loginClicked}>Login</button>
        </div>
        )
    }


}

function ErrorComponent() {
    return <div>An Error Occurred. I don't know what to do! Contact support at abcd-efgh-ijkl</div>
}

function ShowInvalidCredentials(props){
    if(props.hasLoginFailed){
        return <div> Invalid Credentials</div>
    }
    return null
}

function ShowLoginSuccessful(props){
    if(props.showSuccessMessage){
        return <div> Login Successful</div>
    }
    return null
}

export default TodoApp;
